import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./UserList.css"; 

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    console.log(1);
    getUsers();
  }, []);

  const getUsers = async () => {
    try {
      let result = await fetch("/User.json"); 
      result = await result.json();

      const formattedUsers = result.map((user) => ({
        ...user,
        name:`${capitalizeString(user.firstname)} ${user.middlename ? capitalizeString(user.middlename) : ""} ${capitalizeString(user.lastname)} `,
        age: calculateAge(new Date(user.dob)),
      }));

      setUsers(formattedUsers);
      console.log(formattedUsers);
    } catch (err) {
      setError("Failed to fetch users.");
    }
  };


  const capitalizeString = (string) => {
    if (string) {
      return string.charAt(0).toUpperCase() + string.slice(1);
    }
    return string;
  };


  const calculateAge = (dob) => {
    const today = new Date();
    const birthDate = new Date(dob);
    let age = today.getFullYear() - birthDate.getFullYear();
    const m = today.getMonth() - birthDate.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < birthDate.getDate())) {
      age--;
    }
    return age;
  };

  return (
        <div className="user-list-container">
      <h1 className="user-list-title">Users</h1>
      {error && <p className="error-message">{error}</p>}

      <div className="user-card-container">
        {users.map((item, index) => (
          <div className="user-card" key={index}>
            <div className="user-details">
              <p className="user-name">
                <strong>User:</strong> {item.name}
              </p>
              <p>
                <strong>Email:</strong> {item.email}
              </p>
              <p>
                <strong>Age:</strong> {item.age}
              </p>
              <p>
                <strong>Religion:</strong> {item.religion}
              </p>
            </div>
            <Link to={`/viewprofile/${item.id}`}>
              <button className="view-profile-btn">View Profile</button>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UserList;
