import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./UserReport.css";


const UserReport = () => {
    const [reports, setReports] = useState([]);
    const [error, setError] = useState("");

    useEffect(() => {
        console.log(1);
        getReports();
    }, []);

    const getReports = async () => {
        try {
            let result = await fetch("/Report.json");
            result = await result.json();

            const formattedReports = result.map((report) => ({
                ...report,
                name: `${capitalizeString(report.firstname)} ${report.middlename ? capitalizeString(report.middlename) : ""} ${capitalizeString(report.lastname)} `,
                // age: calculateAge(new Date(user.dob)),
            }));

            setReports(formattedReports);
            console.log(formattedReports);
        } catch (err) {
            setError("Failed to fetch users.");
        }
    };


    const capitalizeString = (string) => {
        if (string) {
            return string.charAt(0).toUpperCase() + string.slice(1);
        }
        return string;
    };


    return (
        <div className="user-report-container">
            <h1 className="user-report-title"></h1>
            <div className="report-card-container">
                {reports.map((item, index) => (
                    <div className="report-card" key={index}>
                        <div className="report-details">
                            <p className="user-name">
                                <strong>Type:</strong> {item.reason}
                            </p>
                            <p>
                                <strong>User:</strong> {item.reporter.name}
                            </p>
                            <p>
                                <strong>Email:</strong> {item.reporter.email}
                            </p>
                            <p>
                                <strong>User Reported:</strong> {item.reported.name}
                            </p>
                        </div>
                        <Link to={`/viewreport/${item.id}`}>
                            <button className="view-report-btn">View Report</button>
                        </Link>
                    </div>
                ))}
            </div>
        </div>

    );
};

export default UserReport;
