import React from "react";
import { useLocation } from "react-router-dom";
import { FaUserCircle } from "react-icons/fa";
import "./ViewProfile.css"; 

const ViewProfile = () => {
  const location = useLocation();
  const { user } = location.state || {};

  if (!user) {
    return <div>User data not found. Please go back to the user list.</div>;
  }
  const getUserId = async (userId) => {
    try {
      const token = localStorage.getItem("token");
      console.log("Token retrieved:", token);

      if (!token) {
        setError("User is not authenticated. Please log in again.");
        return;
      }

      const response = await fetch("http://localhost:5000/admin/user-info", {
        method: "GET",
        headers: {
          Authorization: `Bearer ${token}`,
          id: userId
        },
      })
    const result = await response.json();
    } catch (error) {
      console.error("Error fetching users:", error);
      setError("An error occurred. Please try again later.");
    }
  };


  if (!userId) {
    return (
      <div className="profile-container">
        <h2>User not found.</h2>
      </div>
    );
  }

  return (
    <div className="profile-container">
      <div className="profile-card">
        <div className="profile-header">
          <FaUserCircle className="user-icon" />
        </div>
        <div className="profile-details">
          <h2 className="user-name">{user.name}</h2>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Age:</strong> {user.age}</p>
          <p><strong>Religion:</strong> {user.religion}</p>
        </div>
        <div className="profile-actions">
          <button className="btn verified">Verified</button>
          <button className="btn not-verified">Not Verified</button>
        </div>
      </div>
    </div>
  );
};

export default ViewProfile;





















