import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./UserList.css"; 

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    console.log(1);
    getUsers();
  }, []);

  const getUsers = async (e) => {
    e.preventDefault(); 
    try {
        const response = await fetch("http://localhost:5000/admin/all-users", {
            method: "GET",
            authorization:`bearer${JSON.parse(localStorage.getUsers('users'))}`
        });

        const result = await response.json();

        if (response.ok) {
            localStorage.setUsers('users', result.users);

        } else {
            setError(result.message || "User List Not Found. Please try again.");
        }
    } catch (err) {
        setError("An error occurred. Please try again later.");
    }
  };


  const capitalizeString = (string) => {
    if (string) {
      return string.charAt(0).toUpperCase() + string.slice(1);
    }
    return string;
  };


  const calculateAge = (dob) => {
    const today = new Date();
    const birthDate = new Date(dob);
    let age = today.getFullYear() - birthDate.getFullYear();
    const m = today.getMonth() - birthDate.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < birthDate.getDate())) {
      age--;
    }
    return age;
  };

  return (
        <div className="user-list-container">
      <h1 className="user-list-title"></h1>
      {error && <p className="error-message">{error}</p>}

      <div className="user-card-container">
        {users.map((item, index) => (
          <div className="user-card" key={index}>
            <div className="user-details">
              <p className="user-name">
                <strong>User:</strong> {item.name}
              </p>
              <p>
                <strong>Email:</strong> {item.email}
              </p>
              <p>
                <strong>Age:</strong> {item.age}
              </p>
              <p>
                <strong>Religion:</strong> {item.religion}
              </p>
            </div>
            <Link to={`/viewprofile/${item.id}`}>
              <button className="view-profile-btn">View Profile</button>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UserList;
