import React from "react";
import { useParams } from "react-router-dom";
import { FaUserCircle } from "react-icons/fa";
import "./ViewProfile.css"; 

const ViewProfile = () => {
  // const users= [];
  // //   { id: 1, name: "John Doe", email: "john.doe@example.com", age: 34, religion: "Christianity" },
  // //   { id: 2, name: "Jane Mary Smith", email: "jane.smith@example.com", age: 36, religion: "Christianity" },
  // //   { id: 3, name: "Ali Khan", email: "ali.khan@example.com", age: 29, religion: "Islam" },
  // //   { id: 4, name: "Aisha Fatima Ahmed", email: "aisha.ahmed@example.com", age: 26, religion: "Islam" },
  // //   { id: 5, name: "Sharjeena", email: "sharjeena@gmail.com", age: 25, religion: "Islam" },
  // // ];

  // const { userId } = useParams(); 
  // const user = users.find((u) => u.id === parseInt(userId)); 

  const getUserId = async (userId) => {
    try {
      const token = localStorage.getItem("token");
      console.log("Token retrieved:", token);

      if (!token) {
        setError("User is not authenticated. Please log in again.");
        return;
      }

      const response = await fetch("http://localhost:5000/admin/user-info", {
        method: "GET",
        headers: {
          Authorization: `Bearer ${token}`,
          id: userId
        },
      })
    const result = await response.json();
    } catch (error) {
      console.error("Error fetching users:", error);
      setError("An error occurred. Please try again later.");
    }
  };


  if (!userId) {
    return (
      <div className="profile-container">
        <h2>User not found.</h2>
      </div>
    );
  }

  return (
    <div className="profile-container">
      <div className="profile-card">
        <div className="profile-header">
          <FaUserCircle className="user-icon" />
        </div>
        <div className="profile-details">
          <h2 className="user-name">{userId.name}</h2>
          <p><strong>Email:</strong> {userId.email}</p>
          <p><strong>Age:</strong> {userId.age}</p>
          <p><strong>Religion:</strong> {userId.religion}</p>
        </div>
        <div className="profile-actions">
          <button className="btn verified">Verified</button>
          <button className="btn not-verified">Not Verified</button>
        </div>
      </div>
    </div>
  );
};

export default ViewProfile;
