import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./UserList.css";

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    getUsers();
  }, []);

  const getUsers = async () => {
    try {
      const token = JSON.parse(localStorage.getItem("users")); 
      const response = await fetch("http://localhost:5000/admin/all-users", {
        method: "GET",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      const result = await response.json();

      if (response.ok) {
        setUsers(result.users); // Set users in state
        localStorage.setItem("users", JSON.stringify(result.users)); // Correct method
      } else {
        setError(result.message || "User List Not Found. Please try again.");
      }
    } catch (err) {
      setError("An error occurred. Please try again later.");
    }
  };

  const capitalizeString = (string) => {
    if (string) {
      return string.charAt(0).toUpperCase() + string.slice(1);
    }
    return string;
  };

  const calculateAge = (dob) => {
    const today = new Date();
    const birthDate = new Date(dob);
    let age = today.getFullYear() - birthDate.getFullYear();
    const m = today.getMonth() - birthDate.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < birthDate.getDate())) {
      age--;
    }
    return age;
  };

  return (
    <div className="user-list-container">
      <h1 className="user-list-title">User List</h1>
      {error && <p className="error-message">{error}</p>}

      <div className="user-card-container">
        {users.map((item, index) => (
          <div className="user-card" key={index}>
            <div className="user-details">
              <p className="user-name">
                <strong>User:</strong> {capitalizeString(item.name)}
              </p>
              <p>
                <strong>Email:</strong> {item.email}
              </p>
              <p>
                <strong>Age:</strong> {calculateAge(item.dob)} 
              </p>
              <p>
                <strong>Religion:</strong> {capitalizeString(item.religion)}
              </p>
            </div>
            <Link to={`/viewprofile/${item.id}`}>
              <button className="view-profile-btn">View Profile</button>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UserList;
