import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./UserList.css";

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    getUsers();
  }, []);

  const getUsers = async () => {
    try {
      const token = localStorage.getItem("token"); 
      if (!token) {
        setError("User is not authenticated. Please log in again.");
        return;
      }

      const response = await fetch("http://localhost:5000/admin/all-users", {
        method: "GET",
        headers: {
          Authorization: `Bearer ${JSON.parse(token)}`, 
        },
      });

      if (!response.ok) {
        const result = await response.json();
        setError(result.message || "Failed to fetch user list. Please try again.");
        console.error("Error response from server:", result);
        return;
      }

      const result = await response.json();
      setUsers(result.users); 
      localStorage.setItem("users", JSON.stringify(result.users)); 
    } catch (err) {
      console.error("Fetch error:", err);
      setError("An error occurred. Please try again later.");
    }
  };

  const capitalizeString = (string) => {
    return string ? string.charAt(0).toUpperCase() + string.slice(1) : "";
  };

  const calculateAge = (dob) => {
    const today = new Date();
    const birthDate = new Date(dob);
    let age = today.getFullYear() - birthDate.getFullYear();
    const m = today.getMonth() - birthDate.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < birthDate.getDate())) {
      age--;
    }
    return age;
  };

  return (
    <div className="user-list-container">
      <h1 className="user-list-title">User List</h1>
      {error && <p className="error-message">{error}</p>}

      <div className="user-card-container">
        {users.length > 0 ? (
          users.map((item, index) => (
            <div className="user-card" key={index}>
              <div className="user-details">
                <p className="user-name">
                  <strong>User:</strong> {capitalizeString(item.name)}
                </p>
                <p>
                  <strong>Email:</strong> {item.email}
                </p>
                <p>
                  <strong>Age:</strong> {calculateAge(item.dob)}
                </p>
                <p>
                  <strong>Religion:</strong> {capitalizeString(item.religion)}
                </p>
              </div>
              <Link to={`/viewprofile/${item.id}`}>
                <button className="view-profile-btn">View Profile</button>
              </Link>
            </div>
          ))
        ) : (
          !error && <p>No users found.</p>
        )}
      </div>
    </div>
  );
};

export default UserList;
