import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { FaUserCircle } from "react-icons/fa";
import "./ViewProfile.css";

const ViewProfile = () => {
  const { userId } = useParams(); 
  const [user, setUser] = useState(null);
  const [error, setError] = useState("");

  useEffect(() => {
    getUserProfile(userId);
  }, [userId]); 

  const getUserProfile = async (userId) => {
    try {
      const token = localStorage.getItem("token");

      if (!token) {
        setError("User is not authenticated. Please log in again.");
        return;
      }

      const response = await fetch("http://localhost:5000/admin/user-info/"+userId, {
        method: "GET",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      const result = await response.json();

      if (!response.ok) {
        if (response.status === 401) {
          setError("Your session has expired. Please log in again.");
          localStorage.removeItem("token");
        } else {
          setError(result?.message || "Failed to fetch user details.");
        }
        return;
      }

      setUser(result.data);
      setError("");
    } catch (error) {
      console.error("Error fetching user profile:", error);
      setError("An error occurred. Please try again later.");
    }
  };

  if (!user) {
    return (
      <div className="profile-container">
        <h2>{error || "User not found."}</h2>
      </div>
    );
  }


  return (
    <div className="profile-container">
      <div className="profile-card">
        <div className="profile-header">
          <FaUserCircle className="user-icon" />
        </div>
        <div className="profile-details">
          <h2 className="user-name">{user.name}</h2>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Age:</strong> {user.age}</p>
          <p><strong>Religion:</strong> {user.religion}</p>
        </div>
        <div className="profile-actions">
          <button className="btn verified">Verified</button>
          <button className="btn not-verified">Not Verified</button>
        </div>
      </div>
    </div>
  );
};

export default ViewProfile;





















